import '../App.css';
import React, { useEffect, useState } from "react";
import { Link } from 'react-router-dom';

export const Nav = ({userId, chainId, triger}) => {
  const[logined, setLogined] = useState(false);

  useEffect(()=>{
    triger(true);
    console.log('wowwl')
    if(userId !== null)
      setLogined(true);
    else if(sessionStorage.getItem('userid') !== null)
      setLogined(true);
  },[userId])

  const storageClear = () => {
    sessionStorage.clear();
    window.location.reload();
  }
  return (
    <nav>
      <Link to="/"><button>home</button></Link>
      <Link to="/myinfo" >
        <button>myinfo</button>
      </Link>
      {logined ? <button onClick={storageClear}>signout</button>
        :<Link to="/signinpage">
          <button>signin</button>
        </Link>
      }
      {userId} : {chainId}
    </nav>
  );
}
