import { getItem, getFileList } from "../../api";
import { getItemStatus } from "../../utilityUnits/connMintService";

export const setTitleAndDesc = async(_idxId) => {
    const toknStatus = await getItemStatus(_idxId);
    return new Promise(resolve => {
        //console.log(toknStatus);
        const toknURI = toknStatus.fileHash;
        const fileHash = toknURI.split('/')[4];
        getItem(fileHash).then(result => {
            resolve({
                title: result.title,
                desc: result.description,
                hash: result.hash
            })
        })
    })
}
export const getItemFiles = (_fileHash) => {
    console.log(_fileHash);
    getFileList(_fileHash).then(result => {
      return result;
    })
  }
