import React, { useState, useEffect } from "react";
import MintServiceABI from '../contracts/MintService.json';

export const Interaction = ({web3}) => {
  const [mintService, setService] = useState(null);
  useEffect(() => {
    if(web3 !== null){
      const mintService = new web3.eth.Contract(
        MintServiceABI, '0x306a271F178aD37083cDA734F3Aca694FF301B0F'
      )
      setService(mintService);//
      console.log(mintService);
    }
    else{
      console.log('wow');}
  },[])

  const getSymbol = () => {
    // mintService.methods.symbol().call.then(symbol => {
    //   console.log(symbol);
    // })
    mintService.methods.balanceOf('0x7DE7843683CfB91C7bAf49d86165f277DbFE8a3c').call(function (err, res) {

      if (err) {

        console.log("An error occured", err)

        return

      }

      console.log("The balance is: ", res)

    })
  }

  return(
    <>
      <button onClick={getSymbol}>CreateItems</button>
    </>
  )
}
