import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import { ListItemsCpnt } from "../components/itemCpnt";
import { getTokenStatus, getTokenUri, getAmountAll } from "../utilityUnits/connMintService";
//import Web3 from "web3/dist/web3.min.js";

export const HomePage = ({web3}) => {
  const [opendList, setOpend] = useState([]);

  useEffect(() => {
    console.log('wow');
    getOpenedTokn();
    //window.location.reload();
  },[]);

  const getOpenedTokn = async() => {
    const allToknAmount = await getAmountAll(web3);
    let toknStatusArr = [];
    let toknUriArr = [];

    for(let toknId = 0; toknId < allToknAmount; ++toknId){
      console.log(toknId)
      let count = 0;

      const toknStatus = await getTokenStatus(web3, toknId);

      if(toknStatus[0] != 0){
        toknUriArr.push(await getTokenUri(web3, toknId))
        if(toknUriArr[toknId-1] !== toknUriArr[toknId]){
          do{
            toknStatus[0] /= 1000000;
            ++count;
          }while(count < 3)
          toknStatusArr.push({
            tokenId: toknId,
            toknPrice: (toknStatus[0]+' MATIC'),
            toknUri: toknUriArr[toknId]
          })
        }
        setOpend(toknStatusArr);
      }
      else
        toknUriArr.push(null);
    }
  }
  return(
    <>
      <h3>wellcome home</h3>
      <Link to="/filetohash" style={{ textDecoration: 'none' }}>
        <button>create NFT</button>
      </Link>
      {opendList.map((searchItems, index) => (
        <ListItemsCpnt
          key={index}
          toknId={searchItems.tokenId}
          toknUri={searchItems.toknUri}
          status={searchItems.toknPrice}
        />
      ))}
    </>
  )
}
