import React, { useState, useEffect } from "react";
import { onFileInputChange, getFiles } from "../components/uploadFile";
import { fileToHash } from '../components/fileToHash'

export const CreateNft = () => {

  const [fileBuffer, setFileBuffer] = useState();
  const [fileInfo, setFileInfo] = useState([]);
  const [fileHashs, setFileHash] = useState([]);

  useEffect(()=>{
    fileToHash(fileBuffer, setFileHash);
  },[fileInfo])

  const tempbtn = () => {
    console.log('wow');
  }

  const FilesList = (_eachfile) => {
    // fileToHash(fileBuffer, setFileHash);
    return(
      <>
        <p>{_eachfile.title}<br/>{_eachfile.type}</p>
      </>
    )
  }
  return(
    <div className="App">
      <p>extract</p>
      <label className="inputfile"><input type='file' onChange={onFileInputChange} multiple/></label><br/>
      <button onClick={()=>getFiles(setFileBuffer, setFileInfo)}>set file</button>

      {fileInfo.map(uploadItems => (
        <FilesList
          key={uploadItems.name}
          title = {uploadItems.name}
          type = {uploadItems.type}
          size = {uploadItems.size}
        />
      ))}
      {fileHashs.map(files => (
        <p>{files}</p>
      ))}
      <button onClick={tempbtn}>tempbtn</button>
    </div>
  )
}
