import React from "react";
import { useState, useEffect } from "react";
import { 
  EstSetApprovalAll,
  getApproveState,
  setApprovalAll,
} from "../utilityUnits/connMintService";
import { getBalance } from "../utilityUnits/connTradeService";

export const Wallet = ({web3, ADDR}) => {
  const [seller, setSeller] = useState(false);
  const [money, setMoney] = useState();
  const [balance, setBalance] = useState();
  const [rcvAcc ,setRcvAddress] = useState('');
  const [PRIVATE_KEY, setPriv] = useState(null);

  useEffect(() => {
    sellerCheck();
    getUserBal();
  },[]);

  const sellerCheck = async() => {
    getApproveState(ADDR).then(result => {
      setSeller(result);
    })
  }

  const getUserBal = async() => {
    let salesPrice = await getBalance(ADDR);
    web3.eth.getBalance(ADDR).then(balanc => {
      let count = 0;
      do{
        balanc /= 1000000;
        salesPrice /= 1000000;
        ++count;
      }while(count < 3)
      setBalance(balanc);
      setMoney(salesPrice);
    })
  }

  const sendCoin = async() => {
    const nonce = await web3.eth.getTransactionCount(ADDR, 'latest'); // nonce starts counting from 0
    console.log(nonce)
    const transaction = {
     'to': rcvAcc, // faucet address to return eth
     'value': 500000000000000000,
     'gas': 30000,
     'nonce': nonce,
     // optional data field to send message or execute smart contract
    };
    const signedTx = await web3.eth.accounts.signTransaction(transaction, PRIVATE_KEY);

    web3.eth.sendSignedTransaction(signedTx.rawTransaction, function(error, hash) {

      if (!error)
        console.log("🎉 The hash of your transaction is: ", hash, "\n Check Polygon Scan to view the status of your transaction!");
      else
        console.log("❗Something went wrong while submitting your transaction:", error);
    });
  }

  return (
  <>
    <SellerBranch showFlag={seller} ADDR={ADDR} PRIVATE_KEY={PRIVATE_KEY}/>
    <h2>wallet</h2>
    <h4>balance: {balance} MATIC</h4>
    <h4>sales Price: {money} MATIC</h4>
    <h5> PRIVATE_KEY :
      <input onChange={(evt)=>setPriv(evt.target.value)} size='45'></input></h5>
    <h5><button onClick={sendCoin}>send coin</button> :
      <input onChange={(evt)=>setRcvAddress(evt.target.value)} placeholder='receiver Address 0x...' size='45'></input></h5>
  </>
  );
}
const SellerBranch = ({showFlag, ADDR, PRIVATE_KEY}) => {
  const [modalFlag, setFlag] = useState(false);
  const [estGas, setEstGas] = useState();

  const estSetSeller = () => {
    if(PRIVATE_KEY !== null){
      EstSetApprovalAll(ADDR, true).then(result => {
        setFlag(true);
        setEstGas(result);
      })
    }
    else{
      alert('Private Key required');
    }

  }
  const setSeller = () => {
    setApprovalAll(ADDR, PRIVATE_KEY, true).then(result => {
      console.log(result);
      if(result === true)
        alert('Seller Registration Completed');
      else
        alert(`⚠️Err: ${result}`);
    })
    alert('⌛️Processing...');
  }
  return(
    <>{showFlag ? 
      <>
        <h4>Seller State: YES</h4>
      </> : <>
        <h4>Seller State: NO <button onClick={estSetSeller}>Seller Registration</button></h4>
        <SetSellerModal showFlag={modalFlag} estGasFee={estGas} func={setSeller} />
      </>// showFlagがfalseの場合はModalは表示しない)
      }
    </>
  )
}
const SetSellerModal = ({showFlag, estGasFee, func}) => {

  return(<>
    {showFlag ? ( // showFlagがtrueだったらModalを表示する
    <div id="overlay" className='overlay'>
      <div id="modalcontents" className="modalcontents">
        Regist Seller
        <h4>Estimated Gas fee: {estGasFee}e-8 MATIC</h4>
        <button onClick={func}>confirm</button>
      </div>
    </div>
    ) : (
      <></>// showFlagがfalseの場合はModalは表示しない)
    )}
  </>)
}
