import React, { useState }from "react";
import { postUploadArtwork, postUploadItemIdx, getMerge } from "../api";

export const UploadFiles = ({
    fileBufferList,
    fileInfoList,
    finalHash,
    title, desc
  }) => {

  const [uploadState, setState] = useState();

  const uploadItem = () => {
    if(title !== '' && desc !== ''){
      setFileChunks();
      uploadItemIdx();
    }
    else{
      alert('Please enter the title and description of the item to be uploaded')
    }
  }
  const uploadItemIdx = () => {
    const record = {
      title: title,
      desc: desc,
      hash: finalHash
    }
    postUploadItemIdx(record).then(response => {
      console.log(response);
    })
  }
  const setFileChunks = () => {
    const size = 1024 * 50; //50KB 50KB Section size

    fileBufferList.map((file, index) => {
      let fileChunks = [];
      let fileIndex = 0;
      for(let cur = 0; cur < file.length; cur += size) {
        fileChunks.push({
          section: fileIndex++,
          chunk: file.slice(cur, cur + size),
        });
      }
      uploadList(fileChunks, index++);
    })
    mergeChunks();
  }
  const uploadList = (fileChunks, _index) => {
    fileChunks.map((item, index) => {
      const record = {
        fileName: fileInfoList[_index].name,
        section: item.section,
        chunk: item.chunk,
      }
      console.log(record);
      postUploadArtwork(record).then(result => {
        console.log(result);
      })
    })
  }
  const mergeChunks = () => {
    fileInfoList.map((item, idx) => {
      getMerge(item.name, finalHash).then(response => {
        if(response)
          setState('upload complete');
      })
    })
  }
  return(
    <>
      {finalHash == undefined ? <>
      </> : 
      <>
        <h4>uploadfiles.js</h4>
        <button onClick={uploadItem}>upload</button>
      </>}
    </>
  )
}
