import React from 'react';
import { useState, useEffect } from 'react';
import { getOwnedItems, getUserId } from '../api';
import { 
    getSymbol, 
    getTokenUri, 
    getTokenStatus,
} from '../utilityUnits/connMintService';
import { ListItemsCpnt } from './itemCpnt';

export const SetMyNFT = ({chainId}) => {

  const [toknList, setToknList] = useState([]);
  const [toknUri, setToknUri] = useState([]);
  const [toknStatus, setStatus] = useState([]);

  useEffect(()=>{
    toknListViewer();
  },[])


  const toknListViewer = async() => {
    let itemInfoArr = [{title: '', hash: ''}];
    let toknStatusArr = [];

    getUserId(chainId).then(userId => {
      getOwnedItems(userId).then(result => {
        let itemLen = result.length;
        do{
          --itemLen;
          let title = result[itemLen].title;
          let hash = result[itemLen].hash;
          itemInfoArr[itemLen].title = title;
          itemInfoArr[itemLen].hash = hash;
          console.log(itemLen);
        }while(itemLen > 0)
        console.log(itemInfoArr);
      })
    })
    // setToknList(ownedArr);

    // for(let fori = 0; fori < ownedArr.length; ++fori){
    //   toknUriArr.push(await getToknUri(ownedArr[fori]));
    //   toknStatusArr.push(await getToknStatus(ownedArr[fori]))
    // }
    // setToknUri(toknUriArr);
    // setStatus(toknStatusArr);
  }

  const getToknStatus = async(_toknId) => {
    const toknStatus = await getTokenStatus(_toknId);
    let count = 0;
    //console.log(typeof toknStatus[0]);
    do{
      toknStatus[0] /= 100;
      ++count;
    }while(count < 9)

    if(toknStatus[0] != 0)
      return `${toknStatus[0]} MATIC`;
    else
      return 'not for sale';
  }

  return(
    <>
      <h2>owend NFT</h2>
      {toknList.map((searchItems, index) => (
        <ListItemsCpnt
          key={index}
          toknId={searchItems}
          toknUri={toknUri[index]}
          status={toknStatus[index]}
        />
      ))}
    </>
  )
}
