import MintServiceABI from '../contracts/MintService.json';

const mintService = (web3) => {
    const contract_Instance = new web3.eth.Contract(
      MintServiceABI, '0x306a271F178aD37083cDA734F3Aca694FF301B0F'
    )
    return contract_Instance;
}

export const getSymbol = async(web3) => {
    return new Promise(resolve => {
        mintService(web3).methods.symbol().call().then(
        symbol => {
            resolve(symbol);
        },err => { 
            console.log('err: ', err); 
        })
    })
}

export const getOwnedToken = async(web3, ADDR) => {
    return new Promise(resolve => {
        mintService(web3).methods.getOwnedTokn(ADDR).call().then(
            result => {
                resolve(result);
            }
        )
    })
}
export const getTokenUri = async(web3, toknId) => {
    return new Promise(resolve => {
        mintService(web3).methods.tokenURI(toknId).call().then(
            result => {
                resolve(result);
            }
        )
    })
}
export const getTokenStatus = async(web3, toknId) => {
    return new Promise(resolve => {
        mintService(web3).methods.tradeNum(toknId).call().then(
            result => {
                resolve(result);
            }
        )
    })
}
export const getAmountAll = async(web3) => {
    return new Promise(resolve => {
        mintService(web3).methods.getAllToknNum().call().then(
            result => {
                resolve(result);
            }
        )
    })
}
export const EstGasopenTokenBatch = async(web3, SEND_ADDR, fromId, toId, price) => {
    const method = await mintService(web3).methods.openToknBatch(price, fromId, toId);
    const estGas = await method.estimateGas({from: SEND_ADDR});
    return estGas;
}
export const openTokenBatch = async(web3, toknId, toToknId, price, SEND_ADDR, PRIVATE_KEY) => {
    const chainId = await web3.eth.getChainId();
    const nonce = await web3.eth.getTransactionCount(SEND_ADDR, 'latest');

    const method = mintService(web3).methods.openToknBatch(price, toknId, toToknId);
    const gasPrice = await web3.eth.getGasPrice();
    const estGas = await method.estimateGas({from: SEND_ADDR});
    const code = await method.encodeABI();

    const tx = {
        nonce: nonce,
        chainId: chainId,
        from: SEND_ADDR,
        to: '0x306a271F178aD37083cDA734F3Aca694FF301B0F',
        value: '0',
        data: code,
        gasPrice: gasPrice,
        gas: estGas
    }
    signTrx(web3, tx, PRIVATE_KEY);
}
const signTrx = (web3, tx, PRIVATE_KEY) => {
    web3.eth.accounts.signTransaction(tx, PRIVATE_KEY).then(signedTx => {
        web3.eth.sendSignedTransaction(signedTx.rawTransaction, function(error, hash) {
            if (!error)
              if(!alert(`üéâ The hash of your transaction is:  ${hash}\n Check Polygon Scan to view the status of your transaction!`))
                console.log('complete');
            else
              console.log("‚ùóSomething went wrong while submitting your transaction:", error);
        });
    })
}
