import React, { useEffect, useState }from 'react';
import { useParams } from "react-router-dom";
import { getItem } from '../api';
import { getTokenStatus } from '../utilityUnits/connMintService';

export const  ItemDetail = ({web3}) => {
  const locate = useParams();
  const [itemUri, setUri] = useState();
  const [title, setTitle] = useState();
  const [desc, setDesc] = useState();

  useEffect(() => {
    setUri(locate.uri);
    getItemInfo(locate.uri);
  },[title])

  const getItemInfo = () => {
    getItem(itemUri).then(result => {
      console.log(result);
      setTitle(result.title);
      setDesc(result.description);
    })
  }

  return (
  <>
    <h3>itemDetailPage</h3>
    <h4>item uri: {itemUri}</h4>
    <h4>title: {title}</h4>
    <h4>description: {desc}</h4>
  </>
  );
}
