import React from 'react';
import { useState, useEffect } from 'react';
import { getChainId } from '../api';

export const UserInfoPage = () => {
  const uid = sessionStorage.getItem('userid');
  const [chainId, setChainId] = useState('N/A');

  useEffect(()=>{
    getChainId(uid).then(chainId => {
      if(chainId !== null)
        setChainId(chainId);
    })
  },[])

  return(
    <>
      <h2>wellcom {uid}</h2>
      <h5>chainId</h5>
      {chainId == 'N/A' ? <><p>아직 {uid} 계정에 연결되어있는 chainId 가 없습니다.</p></> 
      : <h5>chainId: {chainId}</h5>}
    </>
  )
}
