import { getItem, getFileList, getItemStatus } from "../../api";

export const setTitleAndDesc = async(_itemId) => {
    const toknStatus = await getItemStatus(_idxid);
    //console.log(toknStatus);
    const toknURI = toknStatus.fileHash;
    const fileHash = toknURI.split('/')[4];
    getItem(fileHash).then(result => {
        return {
            title: result.title,
            desc: result.description,
            hash: result.hash
        }
    })
    getFileList(fileHash).then(result => {
        setList(result);
    })
}
