import React from 'react';
import { useState, useEffect } from 'react';
import { getSymbol, getOwnedToken, getTokenUri } from '../utilityUnits/connMintService';

export const SetMyNFT = ({web3, chainId}) => {
  const [toknList, setToknList] = useState([]);
  const [toknUri, setToknUri] = useState([]);

  useEffect(()=>{

  },[])

  const getNftSymbol = async() => {
    console.log(await getSymbol(web3));
  }
  const toknListViewer = async() => {
    let toknUriArr = [];

    const ownedArr = getToknIdList(web3, chainId);
    setToknList(ownedArr);

    for(let fori = 0; fori < ownedArr.length; ++fori){
      toknUriArr.push(await getToknUri(web3, ownedArr[fori]))
    }
    setToknUri(toknUriArr);
  }

  return(
    <>
      <h2>owend NFT</h2>
      <button onClick={toknListViewer}>temp</button>
      {toknList}
      {toknUri}
    </>
  )
}

const getToknIdList = (web3, _chainId) => {
  //const ownedArrStr = await getOwnedToken(web3, _chainId);
  const f = function (a, b) { return a - b }
  //const ownedArr = ownedArrStr.slice().sort(f);
  //.map(str => parseInt(str, 10))
  getOwnedToken(web3, _chainId).then(toknIdArrStr => {
    const ownedArr = toknIdArrStr.slice().sort(f);
    console.log(ownedArr);
    return ownedArr;
  })
  //return ownedArr;
}

const getToknUri = async(web3, _toknId) => {
  return await getTokenUri(web3, _toknId);
}
