import MintServiceABI from '../contracts/MintService.json';
import Web3 from "web3/dist/web3.min.js";
const web3 = new Web3('wss://ws-mumbai.matic.today');
const MintServiceADDR = '0x0C531EC4031F49B8eA9d7FFA75920e7896c2C078';
const MarketPlaceADDR = '0xd13643364b2D2d64e4621602ceC58069e6725564';

const mintService = () => {
    const contract_Instance = new web3.eth.Contract(
      MintServiceABI, MintServiceADDR
    )
    return contract_Instance;
}
const setTrx = async(SEND_ADDR) => {

}

export const hexToUTF8 = async(hex) => {
    try{
        const result = web3.utils.hexToUtf8(hex);
        return result;
    }
    catch(err){
        const result = web3.utils.toAscii(hex);
        return result;
    }
}
export const getSymbol = async(web3) => {
    return new Promise(resolve => {
        mintService(web3).methods.symbol().call().then(
        symbol => {
            resolve(symbol);
        },err => { 
            console.log('err: ', err); 
        })
    })
}
export const getTokenOwner = async(toknId) => {
    return new Promise(resolve => {
        mintService().methods.ownerOf(toknId).call().then(
            result => {
                resolve(result);
            }
        )
    })
}
export const getTokenUri = async(toknId) => {
    return new Promise(resolve => {
        mintService().methods.tokenURI(toknId).call().then(
            result => {
                resolve(result);
            }
        )
    })
}
export const getTokenStatus = async(toknId) => {
    return new Promise(resolve => {
        mintService().methods.tradeNum(toknId).call().then(
            result => {
                resolve(result);
            }
        )
    })
}
export const getAmountAll = async() => {
    return new Promise(resolve => {
        mintService().methods.totalSupply().call().then(
            result => {
                resolve(result);
            }
        )
    })
}
export const getApproveState = async(owner) => {
    return new Promise(resolve => {
        mintService().methods.isApprovedForAll(owner, MarketPlaceADDR).call().then(
            result => {
                resolve(result);
            }
        )
    })
}
export const EstSetApprovalAll = async(SEND_ADDR) => {
    const method = await mintService(web3).methods.setApprovalForAll(MarketPlaceADDR, SEND_ADDR);
    const estGas = await method.estimateGas({from: SEND_ADDR});
    return estGas;
}
export const setApprovalAll = async(agree, PRIVATE_KEY) => {
    const chainId = await web3.eth.getChainId();
    const nonce = await web3.eth.getTransactionCount(SEND_ADDR, 'latest');

    const method = await mintService(web3).methods.setApprovalForAll(MarketPlaceADDR, agree);
    const gasPrice = await web3.eth.getGasPrice();
    const estGas = await method.estimateGas({from: SEND_ADDR});
    const code = await method.encodeABI();

    const tx = {
        nonce: nonce,
        chainId: chainId,
        from: SEND_ADDR,
        to: MintServiceADDR,
        value: '0',
        data: code,
        gasPrice: gasPrice,
        gas: estGas
    }
    signTrx(tx, PRIVATE_KEY);
    return true;
}
export const EstGasopenTokenBatch = async(SEND_ADDR, fromId, toId, price) => {
    const method = await mintService(web3).methods.openToknBatch(price, fromId, toId);
    const estGas = await method.estimateGas({from: SEND_ADDR});
    return estGas;
}
export const openTokenBatch = async(toknId, toToknId, price, SEND_ADDR, PRIVATE_KEY) => {
    const chainId = await web3.eth.getChainId();
    const nonce = await web3.eth.getTransactionCount(SEND_ADDR, 'latest');

    const method = mintService(web3).methods.openToknBatch(price, toknId, toToknId);
    const gasPrice = await web3.eth.getGasPrice();
    const estGas = await method.estimateGas({from: SEND_ADDR});
    const code = await method.encodeABI();

    const tx = {
        nonce: nonce,
        chainId: chainId,
        from: SEND_ADDR,
        to: MintServiceADDR,
        value: '0',
        data: code,
        gasPrice: gasPrice,
        gas: estGas
    }
    const result = signTrx(tx, PRIVATE_KEY);
    console.log(result);
}
const signTrx = (tx, PRIVATE_KEY) => {
    web3.eth.accounts.signTransaction(tx, PRIVATE_KEY).then(signedTx => {
        web3.eth.sendSignedTransaction(signedTx.rawTransaction, function(error, hash) {
            if (!error)
              if(!alert(`🎉 The hash of your transaction is: ${hash}\n Check Polygon Scan to view the status of your transaction!`))
                console.log('complete');
            else
              console.log("❗Something went wrong while submitting your transaction:", error);
        });
    })
}
