import React from 'react';
import { useState, useEffect } from 'react';
import { 
    getSymbol, 
    getOwnedToken, 
    getTokenUri, 
    getTokenStatus,
    openTokenBatch,
    EstGasopenTokenBatch
} from '../utilityUnits/connMintService';
import { ListItemsCpnt } from './itemCpnt';

export const SetMyNFT = ({web3, chainId}) => {
  const [modalFlag, setFlag] = useState(false);
  const [toknList, setToknList] = useState([]);
  const [toknUri, setToknUri] = useState([]);
  const [toknStatus, setStatus] = useState([]);
  const [toknPrice, setPrice] = useState();
  const [startId, setSID] = useState();
  const [endId, setEID] = useState();
  const [PRIVATE_KEY, setPriv] = useState();

  useEffect(()=>{
    toknListViewer();
  },[])

  const getNftSymbol = async() => {
    console.log(await getSymbol(web3));
  }
  const toknListViewer = async() => {
    let toknUriArr = [];
    let toknStatusArr = [];

    const ownedArr = await getToknIdList(web3, chainId);
    setToknList(ownedArr);

    for(let fori = 0; fori < ownedArr.length; ++fori){
      toknUriArr.push(await getToknUri(web3, ownedArr[fori]));
      toknStatusArr.push(await getToknStatus(ownedArr[fori]))
    }
    setToknUri(toknUriArr);
    setStatus(toknStatusArr);
  }

  const getToknStatus = async(_toknId) => {
    const toknStatus = await getTokenStatus(web3, _toknId);
    //console.log(toknStatus[0]);
    if(toknStatus[0] != 0)
      return `${toknStatus[0]} MATIC`;
    else
      return 'not for sale';
  }
  const startToknSale = async() => {
    const price = (toknPrice*100000+'0000000000000');
    const estGas = await EstGasopenTokenBatch(web3, chainId, startId, endId, price);
    console.log(estGas);
    console.log(startId, endId);
    console.log(price);
    console.log(PRIVATE_KEY);
  }

  return(
    <>
      <h5><button onClick={startToknSale}>sale Token</button> 
        : <input placeholder='tokenId' onChange={(evt)=>setSID(evt.target.value)}size='5'/>
        ~<input placeholder='tokenId' onChange={(evt)=>setEID(evt.target.value)}size='5'/>
        price: <input placeholder='price(MATIC)' onChange={(evt)=>setPrice(evt.target.value)} size='10'/>  
      </h5>
      <h5> PRIVATE_KEY :
        <input onChange={(evt)=>setPriv(evt.target.value)} size='45'></input></h5>
      <h2>owend NFT</h2>
      {/* <AlertModal showFlag={modalFlag} setFlag={setFlag} sTs={startToknSale}/> */}
      {toknList.map((searchItems, index) => (
        <ListItemsCpnt
          key={index}
          toknId={searchItems}
          toknUri={toknUri[index]}
          status={toknStatus[index]}
        />
      ))}
    </>
  )
}
const openToken = async(web3, toknId, toToknId, price, SEND_ADDR, PRIVATE_KEY) => {

}
const getToknIdList = async(web3, _chainId) => {
  const f = function (a, b) { return a - b }
  const toknIdArrStr = await getOwnedToken(web3, _chainId);
  const ownedArr = toknIdArrStr.slice().sort(f);
  //.map(str => parseInt(str, 10))
  return ownedArr;
}
const getToknUri = async(web3, _toknId) => {
  return await getTokenUri(web3, _toknId);
}

const AlertModal = ({showFlag, setFlag, startId, endId, price, estGasFee, sTs}) => {
  return(
    <>{showFlag ? ( // showFlagがtrueだったらModalを表示する
      <div id="overlay" className='overlay'>
        <div id="modalcontents" className="modalcontents">
        <p>
          {startId} ~ {endId} 번 토큰을<br/>
          {price}MATIC 가격으로 시장에 공개합니다.<br/>
          예상되는 수수료: {estGasFee}e-18 MATIC
        </p>

        <button onClick={()=>setFlag(false)}>cancel</button>
        <button onClick={sTs}>confirm</button>
        </div>
      </div>
      ) : (
        <></>// showFlagがfalseの場合はModalは表示しない)
      )}
    </>
  )
}
