import React from "react";
import { useState, useEffect } from "react";
import getWeb3 from '../getWeb3';
import Web3 from "web3/dist/web3.min.js";

export const ConnectWeb3 = () => {
  const DEBUG = 1;
  const [accountInfo, setAccInfo] = useState([]);
  const [userAcc ,setAddress] = useState(null);
  const [rcvAcc ,setRcvAddress] = useState('');
  const [account, setAccount] = useState(null);
  const [privKey, setPrivKey] = useState(null);
	const [errorMessage, setErrorMessage] = useState(null);
  const [web3, setWeb3] = useState(null);

  useEffect(() => {
    //connector();
    tempconnect();
  },[]);

  const tempconnect = async() => {
    const web3 = new Web3("https://rpc.maticvigil.com/");
    if(DEBUG) console.log('connected web3 version: ', web3.version);
    setWeb3(web3);
  }
  const connector = async() => {
    try{
      // Get network provider and web3 instance.
      const web3 = await getWeb3();
      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();
      // Get the contract instance.
      const networkId = await web3.eth.net.getId();

      const version = web3.version;
      if(DEBUG) console.log('connected web3 version: ', version);
      setWeb3(web3);
    }
    catch(error){
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  }
  const getUserBal = () => {
    web3.eth.getBalance(userAcc).then(balanc => {
      console.log(balanc);
    })
  }
  const getTrxValue = () => {
    web3.eth.getTransaction('0xd91d66a1c5927ebae746c4d97e8a616fcadb59eb8122f15e160fbab487321dcc').then(result => {
      console.log(result);
    })//0x2b23b695591c5df04f3dd43b1cd2ba31a234ed2913de179cf4a4055acfe133a8 blockhash

  }
  const tempTrx = () => {
    web3.eth.sendTransaction({
      from: account,
      to: '0xDB2c0335930a3F6f58B600935AC1B55b12c3E957',
      value: 12
    }).then(txHash =>{
      web3.eth.getTransaction(txHash.transactionHash).then(result => {
        console.log(result.value);
      })
    })
  }

  const sendCoin = async() => {
    const nonce = await web3.eth.getTransactionCount(rcvAcc, 'latest'); // nonce starts counting from 0

    const transaction = {
     'to': rcvAcc, // faucet address to return eth
     'value': 20000000,
     'gas': 30000,
     'nonce': nonce,
     // optional data field to send message or execute smart contract
    };
    const signedTx = await web3.eth.accounts.signTransaction(transaction, privKey);

    web3.eth.sendSignedTransaction(signedTx.rawTransaction, function(error, hash) {

      if (!error) {

        console.log("ðŸŽ‰ The hash of your transaction is: ", hash, "\n Check Alchemy's Mempool to view the status of your transaction!");

      } else {

        console.log("â—Something went wrong while submitting your transaction:", error);
      }
    });
  }
  const connectWallet = () => {
    window.ethereum.request({ method: "eth_requestAccounts" }).then((result) => {
		setAccount(result[0]);
	  }).catch((error) => {
	  setErrorMessage(error.message);
	  });
  }
  const createAccount = () => {
    const account = web3.eth.accounts.create();
    let accountKey = [];

    if(DEBUG){
      console.log('privateKey : ', account.privateKey);
      console.log('account : ', account.address);
    }
    accountKey.push(account.address);
    accountKey.push(account.privateKey);

    console.log(accountKey);
    setAccInfo(accountKey);
  }
  const tempbtn = () => {
    console.log(userAcc);
    console.log(account);
  }
  const getChainId = () => {
    //console.log(web3.eth.getChainId);
    web3.eth.net.getId().then((x) => {
      switch(x){
        case 1:
          console.log('connected etherium mainnet');
          break;
        case 3:
          console.log('connected ropsten testnet');
          break;
        case 4:
          console.log('connected rinkeby testnet');
          break;
        case 42:
          console.log('connected kovan testnet');
          break;
        case 80001:
          console.log('connected mumbai testnet');
          break;
        default:
          console.log('default type');
      }
    })
  }
  return (
    <>
    <h2>wallet</h2>
    <a onClick={connectWallet}>Connect Wallet</a>
    {account}<br/>
    <button onClick={createAccount}>createAcc</button>
    <button onClick={getUserBal}>UserBal</button>
    <button onClick={sendCoin}>sendCoin</button>
    <button onClick={getChainId}>chain.ver</button>
    <br/>
    <h5>from : 
      <input onChange={(evt)=>setAddress(evt.target.value)} size='45'/><br/></h5>
    <h5>to :
      <input onChange={(evt)=>setRcvAddress(evt.target.value)} size='45'></input></h5>
    <h5>pass :
      <input onChange={(evt)=>setPrivKey(evt.target.value)} size='45'></input></h5>
    <button onClick={tempbtn}>tempbtn</button>
    </>
  );
}
