import React from 'react';
import './card.css';
import { Link } from "react-router-dom";

export const  ListItemsCpnt = ({title, desc, toknId, itemId}) => {
  const sumNailUrl = null;

  return (
    <><div className="ToDoListItem">
      { toknId === undefined ? 
      <Link to={'/itemdetails/' + itemId } style={{ textDecoration: 'none' }}>
      <div className="ToDoListItem-title">title: {title}</div></Link>
      :<Link to={'/itemdetails/' + toknId } style={{ textDecoration: 'none' }}>
      <div className="ToDoListItem-title">title: {title}</div></Link>}

        { sumNailUrl===null ? <></>
          : <div><img src={sumNailUrl} /></div>
        }
      <div className="ToDoListItem-description">description: {desc}</div>
      </div>
    </>
  );
}

export const  ListItemsModal = ({title, desc, toknList, flag}) => {

  // const clickTitle = () => {
  //   toknRange({start: toknList[0], end: toknList.pop()});
  //   flag(true);
  // }
  return (
    <><div className="ToDoListItem">
      <div className="ToDoListItem-title" onClick={()=>flag(true)}>title: {title}</div>
      <div className="ToDoListItem-description">description: {desc}</div>
      </div>
    </>
  );
}
