import React from 'react';
import { useState, useEffect } from 'react';
import { getItemsIdList, getOwnedItems, getUserId } from '../api';
import { 
    getSymbol, 
    getTokenUri, 
    getTokenStatus,
} from '../utilityUnits/connMintService';
import { ListItemsCpnt } from './itemCpnt';

export const SetMyNFT = ({chainId}) => {

  const [toknList, setToknList] = useState([]);
  const [itemList, setItemList] = useState([]);
  const [toknStatus, setStatus] = useState([]);

  useEffect(()=>{
    toknListViewer();
  },[])


  const toknListViewer = async() => {
    let itemInfoArr = [{title: null, hash: null}];
    let toknList = [];

    getUserId(chainId).then(userId => {
      getOwnedItems(userId).then(result => {
        let itemLen = result.length;
        do{
          --itemLen;
          let title = result[itemLen].title;
          let hash = result[itemLen].hash;
          itemInfoArr[itemLen] = {title: title, hash: hash};
          getToknList(userId, hash, toknList);
        }while(itemLen > 0)
        setItemList(itemInfoArr);
        setToknList(toknList);
      })
    })
  }
  const getToknList = async(_userId, _itemHash, _toknList) => {
    let toknIdArr = [];
    getItemsIdList(_userId, _itemHash).then(result => {
      result.map(tokenId => {
        toknIdArr.push(tokenId.toknid);
      })
      //console.log(toknIdArr);
      _toknList.push(toknIdArr);
    })
  }
  const temp = () => {
    console.log(toknList[0]);
  }

  const getToknStatus = async(_toknId) => {
    const toknStatus = await getTokenStatus(_toknId);
    let count = 0;
    //console.log(typeof toknStatus[0]);
    do{
      toknStatus[0] /= 100;
      ++count;
    }while(count < 9)

    if(toknStatus[0] != 0)
      return `${toknStatus[0]} MATIC`;
    else
      return 'not for sale';
  }

  return(
    <>
      <h2>owend NFT</h2>
      <button onClick={temp}>temp</button>
      {itemList.map((searchItems, index) => (
        <ListItemsCpnt
          key={index}
          title={searchItems.title}
          toknList={toknList[index]}
        />
      ))}
    </>
  )
}
