import React from 'react';
import { useState, useEffect } from 'react';
import MintServiceABI from '../contracts/MintService.json';
import { useNavigate } from 'react-router-dom';
import { getUserId, postMintTokn } from '../api';

export const MintItemCpnt = ({web3, toknUri, rightInfo}) => {
  const SEND_ADDR = sessionStorage.getItem('chainid');
  const navigate = useNavigate();
  const [NumOfTokn, setNumOfTokn] = useState();
  const [PRIVATE_KEY, setPrivKey] = useState(null);
  const [mintService, setService] = useState(null);
  const [nonce, setNonce] = useState();
  const [chainId, setChainId] = useState();
  const [modalFlag, setFlag] = useState(false);
  const [estGasFee, setGasFee] = useState();
  const [Royalty, setLowest] = useState();
  const [active, setActive] = useState(false);

  useEffect(() => {
    exclusiveParsing();
    if(web3 !== null && toknUri !==null ){
      setSignData();
    }
  },[web3, toknUri, rightInfo])

  const exclusiveParsing = () => {
    let result = rightInfo.split('/');
    console.log(result[1]);
    if(result[1] === 'Exclusive'){
      setActive(true);
      setNumOfTokn(1);
    }
    else
      setActive(false);
  }
  const setSignData = async() => {
    const mintService = new web3.eth.Contract(
      MintServiceABI, '0x0C531EC4031F49B8eA9d7FFA75920e7896c2C078'
    )
    setService(mintService);

    const nonce = await web3.eth.getTransactionCount(SEND_ADDR, 'latest');
    console.log('nonce: ', nonce);
    setNonce(nonce);

    const chainId = await web3.eth.getChainId();
    console.log('chainId: ', chainId);
    setChainId(chainId); 
  }
  const setNFT = async() => {
    const currentId = await mintService.methods.totalSupply().call();
    const method = mintService.methods.CreateItem(SEND_ADDR, rightInfo+toknUri, NumOfTokn, Royalty*100000+'0000000000000');
    const code = await method.encodeABI();
    const gas = await method.estimateGas({from: SEND_ADDR});
    const gasPrice = await web3.eth.getGasPrice();

    const tx = {
      nonce: nonce,
      chainId: chainId,
      from: SEND_ADDR,
      to: '0x0C531EC4031F49B8eA9d7FFA75920e7896c2C078',
      value: '0',
      data: code,
      gasPrice: gasPrice,
      gas: gas
    };
    //console.log(tx);
    const signedTx = await web3.eth.accounts.signTransaction(tx, PRIVATE_KEY);

    web3.eth.sendSignedTransaction(signedTx.rawTransaction, function(error, hash) {
      if (!error)
        if(!alert(`üéâ The hash of your transaction is: ${hash} \n Check Polygon Scan to view the status of your transaction!`))
          putDBtoknIDX(currentId);
      else
        console.log("‚ùóSomething went wrong while submitting your transaction:", error);
    });
  }
  const putDBtoknIDX = async(currentId) => {
    const userId = await getUserId(SEND_ADDR);
    const record = {
      hash: toknUri,
      toknId: currentId,
      userId: userId,
      mintNum: NumOfTokn
    }
    console.log(record);
    postMintTokn(record).then(result => {
      if(result) navigate("/myinfo");
      else console.log(result);
    })
  }
  const inputDataConfirm = async() => {
    const method = mintService.methods.CreateItem(SEND_ADDR, rightInfo+toknUri, NumOfTokn, Royalty*100000+'0000000000000');
    const gas = await method.estimateGas({from: SEND_ADDR});
    setGasFee(gas);
    setFlag(true);
  }
  return(
    <>
    {/* <button onClick={putDBtoknIDX}>temp</button> */}
    <h4>mintItem.js</h4>
    <h5>num of tokn to mint: <input placeholder='number of token' onChange={(evt)=>setNumOfTokn(evt.target.value)} disabled={active}/></h5>
    <h5>Lowest selling price: <input placeholder='MATIC' onChange={(evt)=>setLowest(evt.target.value)}/></h5>
    <h5>private Key: <input placeholder='input your private key to mint nft' onChange={(evt)=>setPrivKey(evt.target.value)} size='45'/></h5>
    <button onClick={inputDataConfirm}>NFTmint</button>
    <AlertModal 
      showFlag={modalFlag}
      NofTokn={NumOfTokn}
      estGasFee={estGasFee}
      setFlag={setFlag}
      mintTokn={setNFT}
    />
    </>
  )
}

const AlertModal = ({showFlag, setFlag, NofTokn, estGasFee, mintTokn}) => {
  return(
    <>{showFlag ? ( // showFlag„Ååtrue„Å†„Å£„Åü„ÇâModal„ÇíË°®Á§∫„Åô„Çã
      <div id="overlay" className='overlay'>
        <div id="modalcontents" className="modalcontents">
        <p>
          nftÌÜ†ÌÅ∞ÏùÑ {NofTokn}Í∞ú Î∞úÌñâÌï©ÎãàÎã§.<br/>
          ÏòàÏÉÅÎêòÎäî ÏàòÏàòÎ£å: {estGasFee}e-18 MATIC<br/>
          (nftÌÜ†ÌÅ∞ÏùÄ Î∞úÌñâÏßÅÌõÑ ÏïΩ 1~3Î∂Ñ Í≤ΩÍ≥º ÌõÑ ÌôïÏù∏Í∞ÄÎä•)
        </p>
        <button onClick={()=>setFlag(false)}>cancel</button>
        <button onClick={mintTokn}>confirm</button>
        </div>
      </div>
      ) : (
        <></>// showFlag„Ååfalse„ÅÆÂ†¥Âêà„ÅØModal„ÅØË°®Á§∫„Åó„Å™„ÅÑ)
      )}
    </>
  )
}
