import React, { useState } from "react";
import logo from './logo.svg';
import './App.css';
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";
import { CreateNft } from './pages/createNft.js';
import { ConnectWeb3 } from './pages/wallet';
import { HomePage } from './pages/rootPage';
import { UserInfoPage } from "./pages/userInfo";
import { LoginInput, RegistInput } from './pages/loginPage';
import { ItemDetail } from "./pages/itemDtail";
import { Nav } from './components/naviCpnt';

function App() {
  const [triger, setTriger] = useState(false);
  const uid = sessionStorage.getItem('userid');
  const cid = sessionStorage.getItem('chainid');

  return (
    <div className="App">
      <header>
        <Router>
          <Nav userId={uid} chainId={cid}></Nav>
          <section className="">
            <Routes>
              <Route exact path='/' element={<HomePage />}/>
              <Route exact path='/userregist' element={<RegistInput />}/>
              <Route exact path='/signinpage' element={<LoginInput />}/>
              <Route exact path='/myinfo' element={<UserInfoPage chainId={cid}/>}/>
              <Route exact path='/connection' element={<ConnectWeb3 />}/>
              <Route exact path='/filetohash' element={<CreateNft />}/>
              <Route exact path='/itemdetails/:uri/:id' element={<ItemDetail />}/>
            </Routes>
          </section>
        </Router>
      </header>
    </div>
  );
}

export default App;
