import React, { useState } from "react";
import logo from './logo.svg';
import './App.css';
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";
import { CreateNft } from './pages/createNft.js';
import { ConnectWeb3 } from './pages/wallet';
import { HomePage } from './pages/rootPage';
import { UserInfoPage } from "./pages/userInfo";
import { LoginInput, RegistInput } from './pages/loginPage';
import { Nav } from './components/naviCpnt';

function App() {
  const uid = sessionStorage.getItem('userid');
  const [web3, setWeb3] = useState(null);
  const [chainId, setChainId] = useState(null);

  const wireFromRoot = (_linkParam) => {
    setWeb3(_linkParam);
  }

  return (
    <div className="App">
      <header>
        <Router>
          <Nav userId={uid} chainId={chainId}></Nav>
          <section className="">
            <Routes>
              <Route exact path='/' element={<HomePage wiredWeb3={(params) => wireFromRoot(params)}/>}/>
              <Route exact path='/userregist' element={<RegistInput />}/>
              <Route exact path='/signinpage' element={<LoginInput wiredChainId={(param) => setChainId(param)}/>}/>
              <Route exact path='/myinfo' element={<UserInfoPage web3={web3}/>}/>
              <Route exact path='/connection' element={<ConnectWeb3 />}/>
              <Route exact path='/filetohash' element={<CreateNft web3={web3}/>}/>
            </Routes>
          </section>
        </Router>
      </header>
    </div>
  );
}

export default App;
