import React from 'react';
import { useState, useEffect } from 'react';
import MintServiceABI from '../contracts/MintService.json';
import { getChainId } from '../api';

export const MintItemCpnt = ({web3, toknUri}) => {
  const SEND_ADDR = sessionStorage.getItem('chainid');
  const [NumOfTokn, setNumOfTokn] = useState(null);
  const [PRIVATE_KEY, setPrivKey] = useState(null);
  const [mintService, setService] = useState(null);
  const [nonce, setNonce] = useState();
  const [chainId, setChainId] = useState();
  const [modalFlag, setFlag] = useState(false);

  useEffect(() => {
    if(web3 !== null && toknUri !==null ){
      setSignData();
    }
  },[web3, toknUri])

  const setSignData = async() => {
    const mintService = new web3.eth.Contract(
      MintServiceABI, '0x306a271F178aD37083cDA734F3Aca694FF301B0F'
    )
    setService(mintService);

    const nonce = await web3.eth.getTransactionCount(SEND_ADDR, 'latest');
    console.log('nonce: ', nonce);
    setNonce(nonce);

    const chainId = await web3.eth.getChainId();
    console.log('chainId: ', chainId);
    setChainId(chainId); 
  }
  const setNFT = async() => {
    const method = mintService.methods.CreateItem(SEND_ADDR, toknUri, NumOfTokn);
    const code = await method.encodeABI();
    const gas = await method.estimateGas({from: SEND_ADDR});
    const gasPrice = await web3.eth.getGasPrice();

    const tx = {
      nonce: nonce,
      chainId: chainId,
      from: SEND_ADDR,
      to: '0x306a271F178aD37083cDA734F3Aca694FF301B0F',
      value: '0',
      data: code,
      gasPrice: gasPrice,
      gas: gas
    };
    //console.log(tx);
    const signedTx = await web3.eth.accounts.signTransaction(tx, PRIVATE_KEY);

    web3.eth.sendSignedTransaction(signedTx.rawTransaction, function(error, hash) {
      if (!error)
        console.log("üéâ The hash of your transaction is: ", hash, "\n Check Polygon Scan to view the status of your transaction!");
      else
        console.log("‚ùóSomething went wrong while submitting your transaction:", error);
    });
  }
  const inputDataConfirm = () => {

  }
  return(
    <>
    <h4>mintItem.js</h4>
    <h5>num of tokn to mint: <input placeholder='number of token' onChange={(evt)=>setNumOfTokn(evt.target.value)}/></h5>
    <h5>private Key: <input placeholder='input your private key to mint nft' onChange={(evt)=>setPrivKey(evt.target.value)} size='45'/></h5>
    <button onClick={()=>setFlag(true)}>tempb</button>
    <AlertModal />
    </>
  )
}

