import React, { useState, useEffect } from "react";
import { postUploadArtwork, postTemp } from "../api";

export const UploadFiles = ({
    fileBufferList,
    fileInfoList,
    finalHash,
    title, desc,
  }) => {

  const checkParams = () => {
      postTemp().then(result => {
          console.log(result);
      })
  }
  const setFileChunks = () => {
    const size = 1024 * 50; //50KB 50KB Section size
    let fileChunks = [];
    //let index = 0; //Section num
    let fileIndex = 0;

    fileBufferList.map((file, index) => {
      for(let cur = 0; cur < file.length; cur += size) {
        fileChunks.push({
          section: index++,
          chunk: file.slice(cur, cur + size),
        });
      }
      //console.log(fileIndex++);
      uploadList(fileChunks, fileIndex);
    })
  }
  const uploadList = (fileChunks, _index) => {
    fileChunks.map((item, index) => {
      const record = {
        fileName: fileInfoList[_index].name,
        section: item.section,
        chunk: item.chunk,
      }
      postUploadArtwork(record).then(result => {
        console.log(result);
      })
    })
  }
  return(
    <>
      <h5>test-uploadfiles.js</h5>
      <button onClick={checkParams}>checkParams</button>
      <button onClick={setFileChunks}>chunks</button>
    </>
  )
}

