import React, { useEffect, useState } from "react";
import { getFileBolb } from "../../api";

export const FileListViewer = ({fileName, toknUri, permission}) => {
  let count = 0;
  const [preview, setPreview] = useState();

  const ImagePreview = () => {

    const file = fileName.split('.');
      if(count === 0){
        if(file[1] === 'JPG' || file[1] === 'JPEG' || file[1] === 'bmp'){
          getFileBolb(toknUri, fileName).then(result => {
            setPreview(window.URL.createObjectURL(result));
        })
        ++count;
      }
    }

    return(<>
      <img src={preview}  width="300" height="200"/>
    </>)
  }
    const downloadFileData = () => {
      if(permission){
        getFileBolb(toknUri, fileName).then(response => {
          let url = window.URL.createObjectURL(response);
          let a = document.createElement('a');
          a.href = url;
          a.download = fileName;
          a.click();
        })
      }
      else{
        alert('Attempt to download without permission');
      }
    }
    return(
    <>
      <h5>{fileName}<br/>
        <ImagePreview />
        <button onClick={downloadFileData}>download</button>
      </h5>
    </>
    )
}
