import React, { useState, useEffect } from "react";

export const RightBranch = () => {
  const [right, setRight] = useState('');
  const [exclusive, setExclusive] = useState(false);
  const [comm, setComm] = useState(false);

  const checkExclusive = () => {
    if(exclusive) setExclusive(false);
    else setExclusive(true);
  }
  const checkCommercial = (evt) => {
    console.log(evt.target.value);
    if(evt.target.value !== "GE")
      setComm(true);
  }
  const checkRight = (evt) => {
    let rightStr = '';

  }
  const temp = () => {
    console.log(exclusive);
  }
  return(
    <>
      <button onClick={temp}>tempbtn</button>
      <label><input type="checkbox" onChange={checkExclusive}/>
        독점적 이용 허가 </label><br/>
      <label><input type="radio" value="CO" onChange={checkCommercial} name='comm'/>
        상업적 이용 허가 </label>
      <label><input type="radio" value="NC" onChange={checkCommercial} name='comm'/>
        비영리적 이용 허가 </label>
      <label><input type="radio" value="GE" onChange={checkCommercial} name='comm' defaultChecked/>
        단순 소유만 가능 </label><br/>
      {comm ? 
      <>
        허가할 이용 범위 지정
        <label><input type="radio" value="RA" onChange={checkRight} name='right'/>
        모든 권리</label>
        <label><input type="checkbox" value="RR" onChange={checkRight} name='right'/>
        복제권</label>
        <label><input type="checkbox" value="RD" onChange={checkRight} name='right'/>
        배포권</label>
        <label><input type="checkbox" value="RRe" onChange={checkRight} name='right'/>
        대여권</label>
        <label><input type="checkbox" value="RDW" onChange={checkRight} name='right'/>
        2차적 저작물 작성권</label>
        <label><input type="checkbox" value="RE" onChange={checkRight} name='right'/>
        전시권</label>
        <label><input type="checkbox" value="RPP" onChange={checkRight} name='right'/>
        공연권</label>
        <label><input type="checkbox" value="RPT" onChange={checkRight} name='right'/>
        방송권</label>
      </> : <></>}
    </>
  )
}
