import React from 'react';
import './modal.css';
import { useState, useEffect } from 'react';
import { putChainId } from '../api';

export const CreateChainId = ({uid, web3}) => {
  const DEBUG = 1;
  const [modalFlag, setModal] = useState(false);

  const createAccount = () => {
    const account = web3.eth.accounts.create();
    const record = { 
      playerId: uid,
      chainId : account.address
    }

    putChainId(record).then(result => {
      if(result){
        setModal(true);
      }
    })
  }
  const createAccounte = () => {
    setModal(true);
  }

  return(
    <>
      <button onClick={createAccounte}>create chainId</button>
      <AlertModal showFlag={modalFlag} onClose={()=>setModal(false)} />
    </>
  )
}

const AlertModal = ({showFlag, onClose}) => {

    return(
      <>{showFlag ? ( // showFlagがtrueだったらModalを表示する
      <div id="overlay" className='overlay'>
        <div id="modalcontents" className="modalcontents">
        <p>
          🎉Successfuly Created<br/>
          CHAIN ID: account.address<br/>
          PRIVATE KEY: account.privateKey<br/>
          비밀키는 분실 시 되찾을 수 없습니다.<br/>
          반드시 메모장 등 자신만이 알 수 있는 곳에 옮겨 저장하십시오.<br/>
          비밀키는 절대로 타인에게 알려주지 마십시오.<br/>
          *페이지를 새로고침하면 적용됩니다.
        </p>
        </div>
      </div>
      ) : (
        <></>// showFlagがfalseの場合はModalは表示しない)
      )}
      </>
    )
  }
