import React, { useEffect, useState }from 'react';
import { useParams } from "react-router-dom";
import { getItemFiles, setTitleAndDesc } from '../components/detailPages/setItemIDX';
import { FileListViewer } from'../components/detailPages/fileListViewer';
import { getItemId } from '../api';

export const  ItemDetail = () => {
  const FROM_ADDR = sessionStorage.getItem('chainid');
  const locate = useParams();
  const [itemInfo, setII] = useState();
  const [fileList, setFlist] = useState([]);

  useEffect(() => {
    currentMode(locate.id, locate.mode);
  },[])

  const currentMode = (_rowId, _mode) => {
    if(_mode === 'item')
      modeIgetInfo(_rowId);
    else
      console.log('kko');
  }
  const modeIgetInfo = async(_rowId) => {
    const itemIdxId = await getItemId(_rowId);
    const itemInfo = await setTitleAndDesc(itemIdxId);
    setII(itemInfo);
    const fileList = await getItemFiles(itemInfo.hash);
    setFlist(fileList);
  }

  const tempbtn = () => {
    console.log(itemInfo);
  }
  return (
  <><br/><br/>
    <button onClick={tempbtn}>buttonA</button>
    {fileList.map((searchItems, index) => (
      <FileListViewer
        key={index}
        fileName={searchItems}
        toknUri = {itemInfo.hash}
        permission = {true}
      />
    ))}
  <br/><br/></>
  );
}
