import React, { useEffect, useState }from 'react';
import { useParams } from "react-router-dom";
import { getItem, getFileList, getFileBolb, getHash } from '../api';
import { 
  getTokenUri,
  getTokenStatus, 
  getTokenOwner,
  hexToUTF8
} from '../utilityUnits/connMintService';
import { buyNFT } from '../utilityUnits/connTradeService';

export const  ItemDetail = () => {
  const cid = sessionStorage.getItem('chainid');
  const locate = useParams();
  const [status, setStt] = useState({price: null, sts: null});
  const [chkOwn, setChkOwn] = useState(false);
  const [itemUri, setUri] = useState();
  const [lisence, setLis] = useState([]);
  const [title, setTitle] = useState();
  const [desc, setDesc] = useState();
  const [fileList, setList] = useState([]);
  const [modalFlag, setFlag] = useState(false);

  useEffect(() => {
    getToknSts(locate.id);
    getItemInfo(locate.id);
    downloadPermission();
  },[])

  const getToknSts = async(_id) => {
    let count = 0;
    const toknStatus = await getTokenStatus(_id);
    const toknURI = await getTokenUri(_id);
    const splitedUri = toknURI.split('/');
    console.log(toknURI);
    if(splitedUri[3] === ''){
      setLis('Only for personal use')
    }
    else{
      if(splitedUri[2] !== 'CO')
        setLis(splitedUri[3] + ' (No commercially use)');
      else
        setLis(splitedUri[3] + ' (Commercially Avaliable)')
    }
    do{
      toknStatus.price /= 1000000;
    }while(++count < 3)
    hexToUTF8(toknStatus.status).then(result => {
      setStt({price: toknStatus.price, sts: result});
    })
  }

  const downloadPermission = () => {
    getTokenOwner(locate.id).then(result => {
      if(result === cid)
        setChkOwn(true);
    })
  }
  const getItemInfo = async(_id) => {
    const fileHash = await getHash(_id);

    setUri(fileHash);
    getItem(fileHash).then(result => {
      setTitle(result.title);
      setDesc(result.description);
    })
    getItemFiles(fileHash);
  }
  const getItemFiles = (_uri) => {
    getFileList(_uri).then(result => {
      setList(result);
    })
  }
  const temp = () => {
    setFlag(true);
  }
  return (
  <>
    <h3>itemDetailPage</h3>
    <h4>permission to use: <a href= 'http://mintservice.asuscomm.com:3000/helpdesk'>{lisence}</a></h4>
    <h4>title: {title}</h4>
    <h4>description: {desc}</h4>
    <h4>status: {status.sts}</h4>
    <h4>price: {status.price} MATIC</h4>
    <button onClick={temp}>buy this item</button>
    <br/><br/>
    {fileList.map((searchItems, index) => (
        <FileListViewer
          key={index}
          fileName={searchItems}
          toknUri = {itemUri}
          permission = {chkOwn}
        />
    ))}
    <BuyModal
      showFlag={modalFlag}
      ADDR = {cid}
      price = {status.price}
      toknId = {locate.id}
    />
  </>
  );
}
const FileListViewer = ({fileName, toknUri, permission}) => {
  const downloadFileData = () => {
    if(permission){
      getFileBolb(toknUri, fileName).then(response => {
        let url = window.URL.createObjectURL(response);
        let a = document.createElement('a');
        a.href = url;
        a.download = fileName;
        a.click();
      })
    }
    else{
      alert('Attempt to download without permission');
    }
  }
  return(
  <>
    <h5>{fileName}
      <button onClick={downloadFileData}>download</button>
    </h5>
  </>
  )
}
const BuyModal = ({showFlag, ADDR, price, toknId}) => {
  const [PRIVATE_KEY, setPriv] = useState();

  const buyTokn = () => {
    const value = price*100000 + '0000000000000';
    console.log(value);
    console.log(price);
    // buyNFT(toknId, value, ADDR, PRIVATE_KEY).then(
    //   result => {
    //     if(result === true){
    //       alert('🎉Successfully minted');
    //     }
    //     else
    //       alert(`⚠️Err: ${result}`);
    //   }
    // )
  }
  return(<>
    {showFlag ? ( // showFlagがtrueだったらModalを表示する
    <div id="overlay" className='overlay'>
      <div id="modalcontents" className="modalcontents">
        <h4>price: {price}</h4>
        <h5> PRIVATE_KEY :
          <input onChange={(evt)=>setPriv(evt.target.value)} size='45'></input></h5>
        <button onClick={buyTokn}>Buy</button>
      </div>
    </div>
    ) : (
      <></>// showFlagがfalseの場合はModalは表示しない)
    )}
  </>)
}
