import React from 'react';
import { useState, useEffect } from 'react';
import { getChainId } from '../api';
import { CreateChainId } from '../components/createChainId';

export const UserInfoPage = ({web3}) => {
  const uid = sessionStorage.getItem('userid');
  const [chainId, setChainId] = useState('N/A');

  useEffect(()=>{
    getChainId(uid).then(chainId => {
      if(chainId !== null)
        setChainId(chainId);
    })
  },[])

  return(
    <>
      <h2>wellcom {uid}</h2>
      {chainId == 'N/A' ? 
        <>
          <h4>chainId: 아직 {uid} 계정에 연결되어있는 chainId 가 없습니다.</h4>
          <CreateChainId uid={uid} web3={web3}/>
        </> : <>
          <h4>chainId: {chainId}</h4>
        </>}
    </>
  )
}
