import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import { getOpenedItem } from "../api";
import { ListItemsCpnt } from "../components/itemCpnt";

export const HomePage = () => {
  const [opendList, setOpend] = useState([]);
  const [toknIdList, setIdList] = useState([]);

  useEffect(() => {
    getOpenedItems();
    //window.location.reload();
  },[]);

  const getOpenedItems = async() => {
    const itemInfo = await getOpenedItem();
    console.log(itemInfo);
    setOpend(itemInfo);
  }
  const getOpenedTokn = async() => {
    const itemInfo = await getOpenedItem();
    console.log(itemInfo);
    setIdList(itemInfo.toknIdList);
    setOpend(itemInfo.items);
  }
  return(
    <>
      <p style={{textAlign: "center"}}>
      <Link to="/filetohash" style={{ textDecoration: 'none' }}>
        <button style={{fontSize:"large"}}>create NFT</button>
      </Link></p>
      <h3>on sale List</h3>
      <label><input type="radio" value="CO" onChange={getOpenedItems} name='comm'/>
        selling Items </label>
      <label><input type="radio" value="NC" onChange={getOpenedTokn} name='comm'/>
        selling Tokens </label><br/>
      {opendList.map((searchItems, index) => (
        <ListItemsCpnt
          key={index}
          title={searchItems.title}
          desc={searchItems.description}
          toknId = {toknIdList[index]}
          itemId = {searchItems.id}
        />
      ))}
    </>
  )
}
