import React, { useState, useEffect } from "react";
import { onFileInputChange } from "../utilityUnits/fileToBuffer";
import { fileToHash, setFinalHash } from '../utilityUnits/fileToHash'
import { FilesList } from "../components/dispFileList";
import { UploadFiles } from "./uploadFiles";

export const SetFileHash = ({title, desc, pWire}) => {

  let te = -1;
  const [fileBuffer, setFileBuffer] = useState([]);
  const [fileInfo, setFileInfo] = useState([]);
  const [fileHashs, setFileHash] = useState([]);
  const [finalHash, setFinalH] = useState();

  let FILE_HASHS = [];
  let FILE_BUFFER = [];

  useEffect(()=>{

    sortFileSize(FILE_BUFFER).then(result => {
      if(result){
        fileToHash(fileBuffer, setFileHash, setFILE_HASHS);
        const finalHash = getFinalHash();
        setFinalH(finalHash);
        pWire(finalHash);
      }
      else{
        console.log(result);
      }
    })
  },[fileInfo])

  const setFILE_HASHS = (hashArr) => {
    FILE_HASHS = hashArr
    console.log(hashArr);
  }
  const setFILE_BUFFER = (buffer) => {
    FILE_BUFFER = buffer;
    console.log(buffer);
  }

  const sortFileSize = async(_fileBuffer) => {
    let asc = function (a, b) {
      return (a.length < b.length) ? -1 : 1;  //オブジェクトの昇順ソート
    }
    let ascIndex = function(a, b) {
      return (a.size < b.size) ? -1 : 1;
    }

    if(_fileBuffer.length !== 0){
      setFileBuffer(_fileBuffer.sort(asc));
      fileInfo.sort(ascIndex);
      return true;
    }
    else{
      return false;
    }  
  }

  const getFinalHash = () => {
    let fileNum = 0;
    let hashsStr = '';
    let fileTypeArr = [];
    do {
      console.log(fileHashs);
      hashsStr += fileHashs[fileNum];
      fileTypeArr.push((fileInfo[fileNum].type).split('/').pop());
      ++fileNum;
    } while (fileHashs.length > fileNum);

    return setFinalHash(hashsStr);
  }

  return(
    <div>
      <h4>extract</h4>
      <label className="inputfile"><input type='file' onChange={(evt)=>onFileInputChange(evt, setFileBuffer, setFileInfo, setFILE_BUFFER)} multiple/></label><br/>
      <br/>
      {fileInfo.map(uploadItems => (
        <FilesList
          key={uploadItems.name}
          row={++te}
          title = {uploadItems.name}
          type = {uploadItems.type}
          size = {uploadItems.size}
          hash = {fileHashs[te]}
        />
      ))}
      <UploadFiles
        title = {title}
        desc = {desc}
        fileBufferList = {fileBuffer}
        fileInfoList = {fileInfo}
        finalHash = {finalHash}
      />
    </div>
  )
}
