import React from "react";
import { useState, useEffect } from "react";
import { 
  getApproveState,
  getDeposit,
} from "../utilityUnits/connMintService";
import { getTradeBalance } from "../utilityUnits/connTradeService";
import { Help } from "../components/helpCpnt";

export const Wallet = ({web3, ADDR}) => {
  const [flagSend, setSendFlag] = useState(false);
  const [earning, setEarning] = useState();
  const [tradeEarning, setTEarning] = useState();
  const [balance, setBalance] = useState();
  const [rcvAcc ,setRcvAddress] = useState('');
  const [PRIVATE_KEY, setPriv] = useState(null);
  const [modalFlag2, setFlag2] = useState(false);
  const [helpMode, setHelpMod] = useState();

  useEffect(() => {
    getUserBal();
  },[]);

  const getUserBal = async() => {
    let salesPrice = await getDeposit(ADDR);
    let earningTokn = await getTradeBalance(ADDR);
    web3.eth.getBalance(ADDR).then(balanc => {
      let count = 0;
      do{
        balanc /= 1000000;
        salesPrice /= 1000000;
        earningTokn /= 1000000;
        ++count;
      }while(count < 3)
      setBalance(balanc);
      setEarning(salesPrice);
      setTEarning(earningTokn);
    })
  }

  const sendCoin = async() => {
    const nonce = await web3.eth.getTransactionCount(ADDR, 'latest'); // nonce starts counting from 0
    console.log(nonce)
    const transaction = {
     'to': rcvAcc, // faucet address to return eth
     'value': 10000000000000000,
     'gas': 30000,
     'nonce': nonce,
     // optional data field to send message or execute smart contract
    };
    const signedTx = await web3.eth.accounts.signTransaction(transaction, PRIVATE_KEY);

    web3.eth.sendSignedTransaction(signedTx.rawTransaction, function(error, hash) {

      if (!error)
        console.log("üéâ The hash of your transaction is: ", hash, "\n Check Polygon Scan to view the status of your transaction!");
      else
        console.log("‚ùóSomething went wrong while submitting your transaction:", error);
    });
  }

  const helpOpen = (evt, helpMode) => {
    setFlag2(true);
    setHelpMod(helpMode);
    document.addEventListener('click', helpClose);
    evt.stopPropagation();
  }
  const helpClose = () => {
    setFlag2(false);
    document.removeEventListener('click', helpClose);
  }

  return (
  <>
    <h2>wallet</h2>
    <h4>balance: {balance} MATIC
      <button onClick={()=>setSendFlag(true)}>send</button></h4>
    <h4><a style={{cursor: "help"}} onClick={(evt)=>helpOpen(evt, 2)}>Earning price of Item sales: {earning} MATIC</a>
      <button>withdraw</button></h4>
    <h4><a style={{cursor: "help"}} onClick={(evt)=>helpOpen(evt, 2)}>Earning price of NFT sales: {tradeEarning} MATIC</a>
      <button>withdraw</button></h4>
    <h5> PRIVATE_KEY :
      <input onChange={(evt)=>setPriv(evt.target.value)} size='45'></input></h5>
    <h5><button onClick={sendCoin}>send coin</button> :
      <input onChange={(evt)=>setRcvAddress(evt.target.value)} placeholder='receiver Address 0x...' size='45'></input></h5>
    <CoinSendModal
      showFlag = {flagSend}
    />
    <Help
      showFlag={modalFlag2}
      display = {helpMode}
    />
  </>
  );
}
const CoinSendModal = ({showFlag}) => {
  return(<>
    {showFlag ? ( // showFlag„Ååtrue„Å†„Å£„Åü„ÇâModal„ÇíË°®Á§∫„Åô„Çã
    <div id="overlay" className='overlay'>
      <div id="modalcontents" className="modalcontents">
        <p>test.</p>
      </div>
    </div>
    ) : (
      <></>// showFlag„Ååfalse„ÅÆÂ†¥Âêà„ÅØModal„ÅØË°®Á§∫„Åó„Å™„ÅÑ)
    )}
  </>)
}
