import { getItem, getFileList } from "../../api";
import { getItemStatus } from "../../utilityUnits/connMintService";

export const getItemInfo = async(_rowId) => {
    return new Promise(resolve => {
        getItem(_rowId).then(item => {
            resolve({
                title: item.title,
                desc: item.description,
                hash: item.hash,
                itemId: item.itemid
            })
        })
    })
}
export const getItemFiles = (_fileHash) => {
    return new Promise(resolve => {
        getFileList(_fileHash).then(result => {
            resolve(result);
        })
    })
  }
