import React from 'react';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getUserId, postMintTokn } from '../api';
import { EstCreateItem, getAmountAll, mintNFT } from '../utilityUnits/connMintService';

export const MintItemCpnt = ({web3, toknUri, rightInfo}) => {
  const SEND_ADDR = sessionStorage.getItem('chainid');
  const navigate = useNavigate();
  const [NumOfTokn, setNumOfTokn] = useState();
  const [PRIVATE_KEY, setPrivKey] = useState(null);
  const [modalFlag, setFlag] = useState(false);
  const [estGasFee, setGasFee] = useState();
  const [Royalty, setLowest] = useState();
  const [active, setActive] = useState(false);

  useEffect(() => {
    exclusiveParsing();
  },[web3, toknUri, rightInfo])

  const exclusiveParsing = () => {
    let result = rightInfo.split('/');
    console.log(result[1]);
    if(result[1] === 'Exclusive'){
      setActive(true);
      setNumOfTokn(1);
    }
    else
      setActive(false);
  }
  const setNFT = async() => {
    const royality = (Royalty*100000+'0000000000000');
    const tokenUri = rightInfo+toknUri;
    const currentId = await getAmountAll();
    mintNFT(SEND_ADDR, tokenUri, NumOfTokn, royality, PRIVATE_KEY).then(
      result => {
        if(result === true){
          putDBtoknIDX(currentId);
          alert('üéâSuccessfully minted');
        }
        else
          alert(`‚ö†Ô∏èErr: ${result}`);
      }
    )
    //alert('Sales registration completed \n It takes about a minute to reflect the actual reflection');
  }
  const putDBtoknIDX = async(currentId) => {
    const userId = await getUserId(SEND_ADDR);
    const record = {
      hash: toknUri,
      toknId: currentId,
      userId: userId,
      mintNum: NumOfTokn
    }
    console.log(record);
    postMintTokn(record).then(result => {
      if(result) navigate("/myinfo");
      else console.log(result);
    })
  }
  const inputDataConfirm = async() => {
    const royality = (Royalty*100000+'0000000000000');
    const tokenUri = rightInfo+toknUri;
    const estGas = await EstCreateItem(SEND_ADDR, tokenUri, NumOfTokn, royality);
    setGasFee(estGas);
    setFlag(true);
  }
  return(
    <>
    {/* <button onClick={putDBtoknIDX}>temp</button> */}
    <h4>mintItem.js</h4>
    <h5>num of tokn to mint: <input placeholder='number of token' onChange={(evt)=>setNumOfTokn(evt.target.value)} disabled={active}/></h5>
    <h5>Lowest selling price: <input placeholder='MATIC' onChange={(evt)=>setLowest(evt.target.value)}/></h5>
    <h5>private Key: <input placeholder='input your private key to mint nft' onChange={(evt)=>setPrivKey(evt.target.value)} size='45'/></h5>
    <button onClick={inputDataConfirm}>NFTmint</button>
    <AlertModal 
      showFlag={modalFlag}
      NofTokn={NumOfTokn}
      estGasFee={estGasFee}
      setFlag={setFlag}
      mintTokn={setNFT}
    />
    </>
  )
}

const AlertModal = ({showFlag, setFlag, NofTokn, estGasFee, mintTokn}) => {
  return(
    <>{showFlag ? ( // showFlag„Ååtrue„Å†„Å£„Åü„ÇâModal„ÇíË°®Á§∫„Åô„Çã
      <div id="overlay" className='overlay'>
        <div id="modalcontents" className="modalcontents">
        <p>
          nftÌÜ†ÌÅ∞ÏùÑ {NofTokn}Í∞ú Î∞úÌñâÌï©ÎãàÎã§.<br/>
          ÏòàÏÉÅÎêòÎäî ÏàòÏàòÎ£å: {estGasFee}e-8 MATIC<br/>
          (nftÌÜ†ÌÅ∞ÏùÄ Î∞úÌñâÏßÅÌõÑ ÏïΩ 1~3Î∂Ñ Í≤ΩÍ≥º ÌõÑ ÌôïÏù∏Í∞ÄÎä•)
        </p>
        <button onClick={()=>setFlag(false)}>cancel</button>
        <button onClick={mintTokn}>confirm</button>
        </div>
      </div>
      ) : (
        <></>// showFlag„Ååfalse„ÅÆÂ†¥Âêà„ÅØModal„ÅØË°®Á§∫„Åó„Å™„ÅÑ)
      )}
    </>
  )
}
