import React from "react";
import { useState, useEffect } from "react";
import { 
  EstGasopenTokenBatch,
  openTokenBatch,
} from "../utilityUnits/connMintService";

export const Wallet = ({web3, ADDR}) => {
  const scanUrl = `https://mumbai.polygonscan.com/address/${ADDR}`;
  const [modalFlag, setFlag] = useState(false);
  const [balance, setBalance] = useState();
  const [rcvAcc ,setRcvAddress] = useState('');
  const [PRIVATE_KEY, setPriv] = useState(null);
  const [toknPrice, setPrice] = useState();
  const [startId, setSID] = useState();
  const [endId, setEID] = useState();
  const [estGasFee, setGasFee] = useState();

  useEffect(() => {
    getUserBal();
  },[]);

  const getUserBal = () => {
    web3.eth.getBalance(ADDR).then(balanc => {
      let count = 0;
      do{
        balanc /= 1000000;
        ++count;
      }while(count < 3)
      setBalance(balanc);
    })
  }

  const sendCoin = async() => {
    const nonce = await web3.eth.getTransactionCount(ADDR, 'latest'); // nonce starts counting from 0
    console.log(nonce)
    const transaction = {
     'to': rcvAcc, // faucet address to return eth
     'value': 20000000,
     'gas': 30000,
     'nonce': nonce,
     // optional data field to send message or execute smart contract
    };
    const signedTx = await web3.eth.accounts.signTransaction(transaction, PRIVATE_KEY);

    web3.eth.sendSignedTransaction(signedTx.rawTransaction, function(error, hash) {

      if (!error)
        console.log("🎉 The hash of your transaction is: ", hash, "\n Check Polygon Scan to view the status of your transaction!");
      else
        console.log("❗Something went wrong while submitting your transaction:", error);
    });
  }

  return (
  <>
    <a href={scanUrl}>history</a>
    <h2>wallet</h2>
    <h4>balance: {balance} MATIC</h4>
    <h5> PRIVATE_KEY :
      <input onChange={(evt)=>setPriv(evt.target.value)} size='45'></input></h5>
    <h5><button onClick={sendCoin}>send coin</button> :
      <input onChange={(evt)=>setRcvAddress(evt.target.value)} placeholder='receiver Address 0x...' size='45'></input></h5>
  </>
  );
}
const AlertModal = ({showFlag, setFlag, startId, endId, price, estGasFee, sTs}) => {
  return(
    <>{showFlag ? ( // showFlagがtrueだったらModalを表示する
      <div id="overlay" className='overlay'>
        <div id="modalcontents" className="modalcontents">
        <p>
          {startId} ~ {endId} 번 토큰을<br/>
          {price}MATIC 가격으로 시장에 공개합니다.<br/>
          예상되는 수수료: {estGasFee}e-18 MATIC
        </p>

        <button onClick={()=>setFlag(false)}>cancel</button>
        <button onClick={sTs}>confirm</button>
        </div>
      </div>
      ) : (
        <></>// showFlagがfalseの場合はModalは表示しない)
      )}
    </>
  )
}
