import React from "react";
import { useState, useEffect } from "react";
import getWeb3 from '../getWeb3';

export const ConnectWeb3 = () => {
  const DEBUG = 1;
  const [accountInfo, setAccInfo] = useState([]);
  const [userAcc ,setAddress] = useState(null);
  const [account, setAccount] = useState(null);
	const [errorMessage, setErrorMessage] = useState(null);
  const [web3, setWeb3] = useState(null);

  useEffect(() => {
    connector();
  },[]);

  const connector = async() => {
    try{
      // Get network provider and web3 instance.
      const web3 = await getWeb3();
      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();
      // Get the contract instance.
      const networkId = await web3.eth.net.getId();

      const version = web3.version;
      if(DEBUG) console.log(version);
      setWeb3(web3);
    }
    catch(error){
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  }
  const getUserBal = () => {
    web3.eth.getBalance(userAcc).then(balanc => {
      console.log(balanc);
    })
  }
  const getTrxValue = () => {
    web3.eth.getTransaction('0xd91d66a1c5927ebae746c4d97e8a616fcadb59eb8122f15e160fbab487321dcc').then(result => {
      console.log(result);
    })//0x2b23b695591c5df04f3dd43b1cd2ba31a234ed2913de179cf4a4055acfe133a8 blockhash

  }
  const tempTrx = () => {
    web3.eth.sendTransaction({
      from: account,
      to: '0xDB2c0335930a3F6f58B600935AC1B55b12c3E957',
      value: 12
    }).then(txHash =>{
      web3.eth.getTransaction(txHash.transactionHash).then(result => {
        console.log(result.value);
      })
    })
  }
  const connectWallet = () => {
    window.ethereum.request({ method: "eth_requestAccounts" }).then((result) => {
		setAccount(result[0]);
	  }).catch((error) => {
	  setErrorMessage(error.message);
	  });
  }
  const createAccount = () => {
    const account = web3.eth.accounts.create();
    let accountKey = [];

    if(DEBUG){
      console.log('privateKey : ', account.privateKey);
      console.log('account : ', account.address);
    }
    accountKey.push(account.address);
    accountKey.push(account.privateKey);

    console.log(accountKey);
    setAccInfo(accountKey);
  }
  const tempbtn = () => {
    console.log(userAcc);
    console.log(account);
  }
  return (
    <>
    <h2>wallet</h2>
    <a onClick={connectWallet}>Connect Wallet</a>
    {account}<br/>
    <button onClick={createAccount}>createAcc</button>
    <button onClick={getUserBal}>UserBal</button><br/>
    <h5>from : 
    <input onChange={(evt)=>setAddress(evt.target.value)} size='45'/><br/></h5>
    <button onClick={tempbtn}>tempbtn</button>
    </>
  );
}
