import React, { useEffect, useState }from 'react';
import { useParams } from "react-router-dom";
import { getItem, getFileList } from '../api';
import { getTokenStatus } from '../utilityUnits/connMintService';

export const  ItemDetail = ({web3}) => {
  const locate = useParams();
  const [itemUri, setUri] = useState();
  const [title, setTitle] = useState();
  const [desc, setDesc] = useState();
  const [fileList, setList] = useState([]);

  useEffect(() => {
    getItemInfo(locate.uri);
    getItemFiles(locate.uri);
  },[])

  const getItemInfo = (_uri) => {
    getItem(_uri).then(result => {
      setTitle(result.title);
      setDesc(result.description);
      setUri(_uri);
    })
  }
  const getItemFiles = (_uri) => {
    getFileList(_uri).then(result => {
      setList(result);
      console.log(result.length);
    })
  }
  return (
  <>
    <h3>itemDetailPage</h3>
    <h4>item uri: {itemUri}</h4>
    <h4>title: {title}</h4>
    <h4>description: {desc}</h4>
    {fileList.map((searchItems, index) => (
        <FileListViewer
          key={index}
          fileName={searchItems}
          toknUri = {itemUri}
        />
    ))}
  </>
  );
}
const FileListViewer = ({fileName, itemUri}) => {
  const downloadFileData = () => {
    fetch(`https://localhost:8139/home/giparang/threadweb/fortune/server/archive/static/files/${itemUri}/${fileName}`)
      .then(response => {
        response.blob().then(blob => {
          let url = window.URL.createObjectURL(blob);
          let a = document.createElement('a');
          a.href = url;
          a.download = fileName;
          a.click();
      });
    });
  }
  return(
  <>
    <h5>{fileName}
      <button onClick={downloadFileData}>download</button>
    </h5>
  </>
  )
}
