import React, { useContext } from 'react';
import { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import { getItemsIdList, getOwnedItems, getUserId, putOpenStt } from '../api';
import { ListItemsModal } from './itemCpnt';
import { 
  EstGasopenTokenBatch,
  openTokenBatch,
  getTokenStatus,
  getTokenUri,
  getApproveState
} from "../utilityUnits/connMintService";

export const SetMyNFT = ({chainId}) => {

  const [toknList, setToknList] = useState([]);
  const [itemList, setItemList] = useState([]);
  const [modalFlag, setFlag] = useState(false);
  const [toknIdRange, setRange] = useState();

  useEffect(()=>{
    toknListViewer();
  },[])


  const toknListViewer = async() => {
    let itemInfoArr = [{title: null, desc: null}];
    let toknList = [];

    const userId = await getUserId(chainId);
    const item = await getOwnedItems(userId);
    let itemLen = 0;
    do{
      let title = item[itemLen].title;
      let hash = item[itemLen].hash;
      let desc = item[itemLen].description;
      itemInfoArr[itemLen] = {title: title, desc: desc};
      toknList.push(await getToknList(userId, hash));
      ++itemLen;
    }while(itemLen < item.length)
    setToknList(toknList);
    setItemList(itemInfoArr);
  }
  const getToknList = async(_userId, _itemHash) => {
    return new Promise(resolve => {
      let toknIdArr = [];
      getItemsIdList(_userId, _itemHash).then(result => {
        result.map(tokenId => {
          toknIdArr.push(tokenId.toknid);
        })
        resolve(toknIdArr);
      })
    })
  }

  const getToknStatus = async(_toknId) => {
    const toknStatus = await getTokenStatus(_toknId);
    let count = 0;
    //console.log(typeof toknStatus[0]);
    do{
      toknStatus[0] /= 100;
      ++count;
    }while(count < 9)

    if(toknStatus[0] != 0)
      return `${toknStatus[0]} MATIC`;
    else
      return 'not for sale';
  }
  return(
    <>
      <h2>owend NFT</h2>
      {itemList.map((searchItems, index) => (
        <ListItemsModal
          key={index}
          title={searchItems.title}
          desc={searchItems.desc}
          toknList={toknList[index]}
          flag={(tru)=>setFlag(tru)}
          toknRange={(toknId)=>setRange(toknId)}
        />
      ))}
      <SaleModal
        showFlag={modalFlag}
        setFlagFunc={setFlag}
        toknRange={toknIdRange}
        ADDR = {chainId}
      />
    </>
  )
}

const SaleModal = ({showFlag, setFlagFunc, toknRange, ADDR}) => {
  const [PRIVATE_KEY, setPriv] = useState();
  const [toknPrice, setPrice] = useState();
  const [estGasFee, setGasFee] = useState();
  const [flag, setFlag] = useState(false);
  const [lisence, setLis] = useState([]);

  const getLisence = async() => {
    const toknURI = await getTokenUri(toknRange.start);
    setLis(toknURI.split('/'));
  }
  const EstStartToknSale = async() => {
    const seller = await getApproveState(ADDR);
    if(seller){
      if(PRIVATE_KEY){
        const price = (toknPrice*100000+'0000000000000');
        const estGas = await EstGasopenTokenBatch(ADDR, toknRange.start, toknRange.end, price);
        setGasFee(estGas);
        setFlag(true);
      }
      else{
        alert('PRIAVATE KEY required');
      }
    }
    else{
      alert('Seller Registration required');
    }
  }
  const startToknSale = async() => {
    const price = (toknPrice*100000+'0000000000000');
    openTokenBatch(toknRange.start, toknRange.end, price, ADDR, PRIVATE_KEY).then(
      result => {
        if(result === true)
          alert('NFT Sale started');
        else
          alert(`⚠️Err: ${result}`);

      })
    openAllTokn();
    alert('Sales registration completed \n It takes about a minute to reflect the actual reflection');
  }
  const openAllTokn = async() => {
    const record = { toknId: toknRange.start, price: toknPrice};
    putOpenStt(record).then(result => {
      console.log(result);
    })
  }
  const getToknStatus = async() => {
    const status = await getTokenStatus(toknRange.start);
    console.log(status);
  }
  return(<>
    {showFlag ? ( // showFlagがtrueだったらModalを表示する
    <div id="overlay" className='overlay'>
      <div id="modalcontents" className="modalcontents">
        <button onClick={()=>setFlagFunc(false)}>close</button>
        <h4>NFT ID: {toknRange.start} ~ {toknRange.end}</h4>
        <h5>availiable Range: {lisence[3]}<button onClick={getLisence}>check</button></h5>
        <h5> PRIVATE_KEY :
          <input onChange={(evt)=>setPriv(evt.target.value)} size='45'></input></h5>
        <h5> price : 
          <input placeholder='price(MATIC)' onChange={(evt)=>setPrice(evt.target.value)} size='10'/></h5>
        <button onClick={EstStartToknSale}>sale</button>
        <Link to={`/itemdetails/${toknRange.start}`} style={{ textDecoration: 'none' }}>
          <button>check_Detail</button>
        </Link>
        {flag ? 
          <h5> <button onClick={startToknSale}>confirm</button>
            Estimated Gas fee: {estGasFee}e-8 MATIC
          </h5> : <></>}
      </div>
    </div>
    ) : (
      <></>// showFlagがfalseの場合はModalは表示しない)
    )}
  </>)
}
