import React, { useEffect, useState }from 'react';
import { useParams } from "react-router-dom";
import { ItemPage } from '../components/detailPages/itemPage';
import { ToknPage } from '../components/detailPages/toknPage';

export const  ItemDetail = () => {
  const FROM_ADDR = sessionStorage.getItem('chainid');
  const locate = useParams();
  const [toknPage, setPage] = useState(false);

  useEffect(() => {
    if(locate.mode === 'token'){
      setPage(true);}
    else
      setPage(false);
  },[])

  const Renderer = () => {
    if(toknPage){
      return(<ToknPage
        cid = {FROM_ADDR}
        itemId = {locate.id}
        mode = {toknPage}
      />)
    }
    else{
      return(<ItemPage
        cid = {FROM_ADDR}
        itemId = {locate.id}
        mode = {toknPage}
      />)
    }
  }
  const temp = () => {
    console.log(toknPage);
  }
  return (<>
    <button onClick={temp}>temp</button>
    <Renderer />
  </>
  );
}
