import React, { useState, useEffect } from "react";
import { onFileInputChange } from "../utilityUnits/fileToBuffer";
import { fileToHash, setFinalHash } from '../utilityUnits/fileToHash'
import { FilesList } from "../components/dispFileList";
import { postUploadArtwork } from "../api";

export const SetFileHash = () => {

  let te = -1;
  const [fileBuffer, setFileBuffer] = useState([]);
  const [fileInfo, setFileInfo] = useState([]);
  const [fileHashs, setFileHash] = useState([]);
  const [finalHash, setFinalH] = useState();
  const [fileType, setType] = useState([]);

  useEffect(()=>{
    setTimeout(() => {
      sortFileSize().then(result => {
        if(result) fileToHash(fileBuffer, setFileHash);
      })
    }, 250);
  },[fileInfo])

  const sortFileSize = async() => {
    let asc = function (a, b) {
      return (a.length < b.length) ? -1 : 1;  //オブジェクトの昇順ソート
    }
    if(fileBuffer.length !== 0){
      fileBuffer.sort(asc);
      return true;
    }
    else{
      return false;
    }  
  }

  const getFinalHash = () => {
    let fileNum = 0;
    let hashsStr = '';
    let fileTypeArr = [];
    do {
      hashsStr += fileHashs[fileNum];
      fileTypeArr.push((fileInfo[fileNum].type).split('/').pop());
      ++fileNum;
    } while (fileHashs.length > fileNum);

    setType(fileTypeArr);
    return setFinalHash(hashsStr);
  }
  const uploadItem = () => {
    console.log(getFinalHash());
  }
  const tempbtn = () => {
    console.log(fileBuffer);
  }
  return(
    <div>
      <h4>extract</h4>
      <label className="inputfile"><input type='file' onChange={(evt)=>onFileInputChange(evt, setFileBuffer, setFileInfo)} multiple/></label><br/>
      <br/>
      {fileInfo.map(uploadItems => (
        <FilesList
          key={uploadItems.name}
          row={++te}
          title = {uploadItems.name}
          type = {uploadItems.type}
          size = {uploadItems.size}
          hash = {fileHashs[te]}
        />
      ))}
      <button onClick={uploadItem}>uploadFiles</button>
      <button onClick={tempbtn}>uploadFiles</button>
    </div>
  )
}
const uploadFiles = (_title, _desc, _hash, _typeArr, _fileBuffer) => {
    const record = {
      title: _title,
      desc: _desc,
      hash: _hash,
      typeArr: _typeArr,
      fileBuffer: _fileBuffer
    }
