import React from "react";
import { useState, useEffect } from "react";
import { EstGasopenTokenBatch } from "../utilityUnits/connMintService";

export const Wallet = ({web3, ADDR}) => {
  const DEBUG = 1;
  const [balance, setBalance] = useState();
  const [rcvAcc ,setRcvAddress] = useState('');
  const [PRIVATE_KEY, setPriv] = useState(null);
  const [toknPrice, setPrice] = useState();
  const [startId, setSID] = useState();
  const [endId, setEID] = useState();

  useEffect(() => {
    getUserBal();
  },[]);

  const getUserBal = () => {
    web3.eth.getBalance(ADDR).then(balanc => {
      let count = 0;
      do{
        balanc /= 1000000;
        ++count;
      }while(count < 3)
      setBalance(balanc);
    })
  }

  const sendCoin = async() => {
    const nonce = await web3.eth.getTransactionCount(ADDR, 'latest'); // nonce starts counting from 0
    console.log(nonce)
    const transaction = {
     'to': rcvAcc, // faucet address to return eth
     'value': 20000000,
     'gas': 30000,
     'nonce': nonce,
     // optional data field to send message or execute smart contract
    };
    const signedTx = await web3.eth.accounts.signTransaction(transaction, PRIVATE_KEY);

    web3.eth.sendSignedTransaction(signedTx.rawTransaction, function(error, hash) {

      if (!error)
        console.log("ðŸŽ‰ The hash of your transaction is: ", hash, "\n Check Polygon Scan to view the status of your transaction!");
      else
        console.log("â—Something went wrong while submitting your transaction:", error);
    });
  }
  const EstStartToknSale = async() => {
    const price = (toknPrice*100000+'0000000000000');
    const estGas = await EstGasopenTokenBatch(chainId, startId, endId, price);
    setGasFee(estGas);
    setFlag(true);
  }
  return (
    <>
    <h2>wallet</h2>
    <h4>balance: {balance} MATIC</h4>
    <h5> PRIVATE_KEY :
      <input onChange={(evt)=>setPriv(evt.target.value)} size='45'></input></h5>
    <h5><button onClick={sendCoin}>send coin</button> :
      <input onChange={(evt)=>setRcvAddress(evt.target.value)} placeholder='receiver Address 0x...' size='45'></input></h5>
    <h5><button onClick={EstStartToknSale}>sale Token</button> 
      : <input placeholder='tokenId' onChange={(evt)=>setSID(evt.target.value)}size='5'/>
      ~<input placeholder='tokenId' onChange={(evt)=>setEID(evt.target.value)}size='5'/>
      price: <input placeholder='price(MATIC)' onChange={(evt)=>setPrice(evt.target.value)} size='10'/>  
    </h5>
    </>
  );
}
